mapper.select:
SELECT * FROM ( SELECT rownum as rnum, n.* FROM ( SELECT userid ,uservnum ,username ,userbirth ,userphone ,disabled ,compliment_count ,matching_flag FROM tasukete_user
WHERE ${searchItem} LIKE '%'||#{searchWord}||'%'
ORDER BY username ) n ) WHERE rnum between #{srow} and #{erow}

mapper.update:
UPDATE tasukete_user
SET
	username = #{username}
	,userbirth = #{userbirth}
	,userphone = #{userphone}
	,disabled = #{disabled}
WHERE
	userid = #{userid}
